// Trigger updates when the Web Form loads
frappe.web_form.after_load = () => {
    update_display(); // Initial update when the form loads
};

// Trigger updates when `registration_type` changes
frappe.web_form.on('registration_type', () => {
    update_display(); // Re-apply logic when the user changes the registration type
});

function update_display() {
    const registrationType = frappe.web_form.get_value('registration_type');

    // Define field groups
    const commonFields = [
        'email',
        'phone_number',
        'grade',
        'previous_experience',
        'about_previous_experience_if_yes',
        'history_section'
    ];
    const advisorFields = ['advisor_email', 'advisor_phone_number'];
    const committeeFields = ['preferred_committee', 'delegate_preferences_section', '__section_6', '__section_8','__section_9','__section_10']; // Fields related to committee preferences
    const mandatoryFields = ['full_name', 'school_name'];

    if (registrationType === 'As a School') {
        // Hide common fields, including committee preferences
        // update_field_group([...commonFields, ...committeeFields], { action: 'hide', required: false, value: '' });
        update_field_group(commonFields, { action: 'hide', required: false });
        update_field_group(committeeFields, { action: 'hide', required: false });

        // Show advisor fields and mark as required
        update_field_group(advisorFields, { action: 'show', required: true});

        // Ensure mandatory fields are visible and required
        update_field_group(mandatoryFields, { action: 'show', required: true });
    } else if (registrationType === 'Individually') {
        // Show all common fields
        update_field_group(commonFields, { action: 'show', required: true });

        // Show committee preference fields
        update_field_group(committeeFields, { action: 'show', required: true });

        // Hide advisor fields
        update_field_group(advisorFields, { action: 'hide', required: false });

        // Ensure mandatory fields are visible and required
        update_field_group(mandatoryFields, { action: 'show', required: true });
    }
}

// Helper function to update a group of fields
function update_field_group(fields, { action, required = false, value = null }) {
    fields.forEach(fieldname => {
        if (action === 'hide') {
            hide_field(fieldname);
        } else if (action === 'show') {
            show_field(fieldname);
        }

        if (required !== null) {
            set_field_required(fieldname, required);
        }

        if (value !== null) {
            frappe.web_form.set_value(fieldname, value);
        }
    });
}

// Hide a single field
function hide_field(fieldname) {
    const field = frappe.web_form.fields_dict[fieldname];
    if (field) {
        field.df.hidden = true; // Set the field to hidden
        field.refresh();
    }
}

// Show a single field
function show_field(fieldname) {
    const field = frappe.web_form.fields_dict[fieldname];
    if (field) {
        field.df.hidden = false; // Make the field visible
        field.refresh();
    }
}

// Set a single field as required or not
function set_field_required(fieldname, required) {
    const field = frappe.web_form.fields_dict[fieldname];
    if (field) {
        field.df.reqd = required; // Set the field as required or not
        field.refresh();
    }
}
